import React from "react";
import { motion } from "framer-motion";
import { AnimatePresence } from "framer-motion";
import "./leader.css";

const Sentence = {
  hidden: {
    color: "#f26822",
    diplay: "none",
  },
  visible: {
    diplay: "inline",
    transition: {
      staggerChildren: 0.08,
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
  },
};
class Leader extends React.Component {
  constructor(props) {
    super(props);
    this.state = { abc: 0 };
  }

  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState((prevState) => {
      return { abc: prevState.abc };
    });
  }
  render() {
    const industries = ["Corporate IT Resource", "IT Staffing"];

    return (
      <section className="leaderSection" onLoad={this.onLoad}>
        <div className="container">
          <span className="title">We Are A Leader In</span>{" "}
          <div>
            <span style={{ color: "#fff" }}>{this.state.abc()}</span>
            {industries.map((ind, index) => {
              return (
                <motion.div
                  className="title"
                  variants={Sentence}
                  initial="hidden"
                  whileInView="visible"
                  style={{ display: "inline" }}
                >
                  <span>{index}</span>
                  {ind.split("").map((char, index) => {
                    return (
                      <motion.span variants={Letter} className="industryName">
                        {char}
                      </motion.span>
                    );
                  })}
                </motion.div>
              );
            })}
          </div>
          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
