import React from "react";
import { motion } from "framer-motion";
import "./leader.css";

const Sentence = {
  hidden: {
    opacity: 1,
  },
  visibile: {
    opacity: 1,
    transition: {
      delay: 0.5,
      staggerChildren: 0.09,
      when: "beforeChildren",
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visibile: {
    opacity: 1,
  },
};
class Leader extends React.Component {
  render() {
    const industry = "IT Staffing";

    return (
      <section className="leaderSection">
        <div className="container">
          <div className="title">
            <span>We Are A Leader In </span>
            <motion.span
              variants={Sentence}
              initial="hidden"
              animate="visible"
              className="industryName"
            >
              {industry.split("").map((char, index) => {
                return (
                  <motion.span key={char + "-" + "index"} variants={Letter}>
                    {char}
                  </motion.span>
                );
              })}
            </motion.span>
          </div>

          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
