import React from "react";
import { motion } from "framer-motion";
import { AnimatePresence } from "framer-motion";
import "./leader.css";

const Sentence = {
  hidden: {
    color: "#f26822",
    diplay: "none",
  },
  visible: {
    diplay: "inline",
    transition: {
      staggerChildren: 0.08,
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
  },
};
class Leader extends React.Component {
  constructor(props) {
    super(props);
    this.setState({ currentIndustry: 0 });
  }
  render() {
    const industries = ["Corporate IT Resource", "IT Staffing"];

    setTimeout(() => {
      this.setState((state) => {
        return {
          currentIndustry:
            state.currentIndustry < industries.length - 1
              ? state.currentIndustry + 1
              : 0,
        };
      });
      console.log(this.state.currentIndustry);
    }, 1000);
    return (
      <section className="leaderSection">
        <div className="container">
          <span className="title">We Are A Leader In</span>{" "}
          <div>
            {industries.map((ind, index) => {
              return (
                <motion.div
                  className="title"
                  variants={Sentence}
                  initial="hidden"
                  whileInView="visible"
                  style={{ display: "inline" }}
                >
                  {ind.split("").map((char, index) => {
                    return (
                      <motion.span variants={Letter} className="industryName">
                        {char}
                      </motion.span>
                    );
                  })}
                </motion.div>
              );
            })}
          </div>
          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
