import React from "react";
import { motion } from "framer-motion";
import { AnimatePresence } from "framer-motion";
import "./leader.css";
const Sentences = {
  hidden: {
    opacity: 1,
  },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 2,
    },
  },
};
const Sentence = {
  hidden: {
    color: "#f26822",
    diplay: "none",
  },
  visible: {
    diplay: "inline",
    transition: {
      staggerChildren: 0.08,
      when: "beforeChildren",
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
    transition: {
      yoyo: 5,
    },
  },
};
class Leader extends React.Component {
  render() {
    const industries = ["Corporate IT Resource", "IT Staffing"];
    const industry = "IT Staffing";

    return (
      <section className="leaderSection">
        <div className="container">
          <span className="title">We Are A Leader In</span>{" "}
          <motion.div
            variants={Sentences}
            initial="hidden"
            whileInView="visible"
          >
            {industries.map((ind) => {
              return (
                <AnimatePresence>
                  <motion.div
                    className="title"
                    variants={Sentence}
                    style={{ display: "inline" }}
                    exit={{ display: "none" }}
                  >
                    {ind.split("").map((char, index) => {
                      return (
                        <motion.span variants={Letter} className="industryName">
                          {char}
                        </motion.span>
                      );
                    })}
                  </motion.div>
                </AnimatePresence>
              );
            })}
          </motion.div>
          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
