import React from "react";
import { motion } from "framer-motion";
import "./leader.css";
import { redirect } from "react-router-dom";

const Sentence = {
  hidden: {
    opacity: 1,
  },
  visible: {
    opacity: 1,
    backgroundColore: "red",
    transition: {
      staggerChildren: 0.2,
      when: "afterChildren",
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
    transition: {},
    type: "spring",
  },
};
class Leader extends React.Component {
  render() {
    const industry = "IT Staffing";

    return (
      <section className="leaderSection">
        <div className="container">
          <span className="title">We Are A Leader In </span>
          <motion.div
            className="title"
            variants={Sentence}
            initial="hidden"
            whileInView="visible"
            style={{ display: "inline" }}
          >
            {industry.split("").map((char, index) => {
              return (
                <motion.span variants={Letter} className="industryName">
                  {char}
                </motion.span>
              );
            })}
          </motion.div>

          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
