import { motion } from "framer-motion";
import React from "react";
import "./infoSection.css";

const firstMotion = {
  hidden: {
    opacity: 0,
    x: "-100%",
  },
  visible: {
    opacity: 1,
    x: 0,
    transition: {
      duration: 2,
      delay: 0.2,
      staggerChildren: 2,
      type: "spring",
      stiffness: 80,
    },
  },
};

const secondMotion = {
  hidden: {
    opacity: 0,
    x: "-100%",
  },
  visible: {
    opacity: 1,
    x: 0,
    transition: {
      duration: 1,
      delay: 0.2,
      type: "spring",
      stiffness: 60,
    },
  },
};
class InfoSection extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <section className="section">
        <motion.div
          className="intro-section"
          variants={firstMotion}
          initial="hidden"
          whileInView="visible"
        >
          <motion.div varints={secondMotion} className="intro-section-details">
            <div className="section-name">{this.props.name}</div>
            <div className="section-title">{this.props.title}</div>
            <div className="section-title-seperator"></div>
            <div className="section-description">{this.props.children}</div>
          </motion.div>
          <motion.div variants={firstMotion} className="intro-section-img">
            <img src={this.props.image} alt="intro image" />
          </motion.div>
        </motion.div>
      </section>
    );
  }
}
export default InfoSection;
