import { motion } from "framer-motion";
import React from "react";
import "./infoSection.css";
import appAnimations from "./common/js/animations.js";
class InfoSection extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <section className="section">
        <motion.div
          className="intro-section"
          variants={appAnimations.slideIn}
          initial="hidden"
          whileInView="visible"
        >
          <motion.div
            varints={appAnimations.slideIn}
            className="intro-section-details"
          >
            <div className="section-name">{this.props.name}</div>
            <div className="section-title">{this.props.title}</div>
            <div className="section-title-seperator"></div>
            <div className="section-description">{this.props.children}</div>
          </motion.div>
          <motion.div
            variants={appAnimations.slideInTwo}
            className="intro-section-img"
          >
            <img src={this.props.image} alt="intro image" />
          </motion.div>
        </motion.div>
      </section>
    );
  }
}
export default InfoSection;
