import React from "react";
import { motion } from "framer-motion";
import "./leader.css";
import { redirect } from "react-router-dom";
const Sentences = {
  hidden: {
    opacity: 1,
  },
  visible: {
    opacity: 0,
    transition: {
      staggerChildren: 1,
    },
  },
};
const Sentence = {
  hidden: {
    color: "#f26822",
    opacity: 1,
  },
  visible: {
    opacity: 1,
    color: "#888",
    transition: {
      staggerChildren: 0.08,
      when: "afterChildren",
    },
  },
};

const Letter = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
  },
};
class Leader extends React.Component {
  render() {
    const industries = ["Corporate IT Resource", "IT Staffing"];
    const industry = "IT Staffing";

    return (
      <section className="leaderSection">
        <div className="container">
          <span className="title">We Are A Leader In</span>{" "}
          {industries.map((ind) => {
            return (
              <motion.div
                className="title"
                variants={Sentence}
                initial="hidden"
                whileInView="visible"
                style={{ display: "inline" }}
              >
                {ind.split("").map((char, index) => {
                  return (
                    <motion.span variants={Letter} className="industryName">
                      {char}
                    </motion.span>
                  );
                })}
              </motion.div>
            );
          })}
          <div className="sub-text">
            BrainHR IT Solutions provides services that improve the way business
            is done in the world.
          </div>
        </div>
      </section>
    );
  }
}
export default Leader;
